import { useState, useEffect, useRef } from "react";
import { Canvas, useFrame } from "@react-three/fiber";
import { OrbitControls, Text, Float, Stars } from "@react-three/drei";
import * as THREE from "three";
import Confetti from "react-confetti";
import CodeMirror from "@uiw/react-codemirror";
import { python } from "@codemirror/lang-python";
import "./App.css";

function FloatingCube({ position, color }) {
  const meshRef = useRef();
  useFrame((state, delta) => {
    meshRef.current.rotation.x += delta * 0.5;
    meshRef.current.rotation.y += delta * 0.3;
  });
  return (
    <mesh ref={meshRef} position={position}>
      <boxGeometry args={[1, 1, 1]} />
      <meshStandardMaterial color={color} />
    </mesh>
  );
}

function FloatingText({ text, position, size = 0.5, color = "white" }) {
  return (
    <Float speed={2} rotationIntensity={0.5} floatIntensity={1}>
      <Text
        position={position}
        fontSize={size}
        color={color}
        font="https://fonts.gstatic.com/s/raleway/v14/1Ptrg8zYS_SKggPNwK4vaqI.woff"
      >
        {text}
      </Text>
    </Float>
  );
}

function App() {
  const [username, setUsername] = useState("");
  const [isPlaying, setIsPlaying] = useState(false);
  const [gameTimeLeft, setGameTimeLeft] = useState(900); // 15 minutes in seconds
  const [score, setScore] = useState(0);
  const [challengeId, setChallengeId] = useState(1);
  const [solvedCount, setSolvedCount] = useState(0);
  const [code, setCode] = useState("");
  const [feedback, setFeedback] = useState("");
  const [solution, setSolution] = useState("");
  const [showConfetti, setShowConfetti] = useState(false);
  const [isGameOver, setIsGameOver] = useState(false);

  // Sample challenges (normally from backend)
  const challenges = [
    "def is_positive(n):\n    if n > 0:\n        return True\n    return False",
    "def max_of_two(a, b):\n    if a > b:\n        return a\n    else:\n        return b",
    // Add more challenges as needed (up to 10 for simplicity)
  ];

  useEffect(() => {
    if (isPlaying && gameTimeLeft > 0) {
      const timer = setInterval(() => {
        setGameTimeLeft((prev) => {
          if (prev <= 1) {
            setIsGameOver(true);
            setIsPlaying(false);
            setShowConfetti(true);
            clearInterval(timer);
            return 0;
          }
          return prev - 1;
        });
      }, 1000);
      return () => clearInterval(timer);
    }
  }, [isPlaying, gameTimeLeft]);

  const formatTime = (seconds) => {
    const mins = Math.floor(seconds / 60);
    const secs = seconds % 60;
    return `${mins}:${secs < 10 ? "0" : ""}${secs}`;
  };

  const startGame = () => {
    if (username.trim()) {
      setIsPlaying(true);
      setChallengeId(1);
      setCode(challenges[0]);
    }
  };

  const evaluateCode = () => {
    // Simplified evaluation (normally backend)
    const optimized = code.includes("return n > 0") || code.includes("return max(a, b)");
    const newScore = optimized ? 10 : 0;
    setScore(score + newScore);
    setFeedback(optimized ? "Great optimization!" : "Try to make it more efficient!");
    setSolvedCount(solvedCount + 1);
    if (solvedCount + 1 < challenges.length) {
      setChallengeId(challengeId + 1);
      setCode(challenges[solvedCount + 1]);
    } else {
      setIsGameOver(true);
      setIsPlaying(false);
      setShowConfetti(true);
    }
  };

  return (
    <div className="app-container">
      {showConfetti && <Confetti />}

      <div className="three-canvas-container">
        <Canvas camera={{ position: [0, 0, 10], fov: 50 }}>
          <ambientLight intensity={0.5} />
          <pointLight position={[10, 10, 10]} />
          <Stars radius={100} depth={50} count={5000} factor={4} saturation={0} fade speed={1} />
          {!isPlaying && !isGameOver ? (
            <>
              <FloatingText text="Refactor Royale" position={[0, 2, 0]} size={1} color="#00ffff" />
              <FloatingCube position={[-3, 0, 0]} color="#ff00ff" />
              <FloatingCube position={[3, 0, 0]} color="#ffff00" />
              <FloatingText text="Enter your name" position={[0, -1, 0]} size={0.5} color="#ffffff" />
            </>
          ) : isPlaying ? (
            <>
              <FloatingText text={`Challenge ${challengeId}`} position={[0, 5, 0]} size={0.7} color="#00ff00" />
              <FloatingText text={`Score: ${score}`} position={[-5, 4, 0]} size={0.5} color="#ffff00" />
              <FloatingText text={`Time: ${formatTime(gameTimeLeft)}`} position={[5, 4, 0]} size={0.5} color="#ff00ff" />
            </>
          ) : (
            <FloatingText text="CONGRATULATIONS!" position={[0, 0, 0]} size={1.5} color="#00ffff" />
          )}
          <OrbitControls enableZoom={false} />
        </Canvas>
      </div>

      {!isPlaying && !isGameOver ? (
        <div className="login-wrapper">
          <div className="login-container glassmorphic">
            <h1 className="neon-text">Refactor Royale</h1>
            <input
              type="text"
              placeholder="Enter your username"
              value={username}
              onChange={(e) => setUsername(e.target.value)}
              className="neon-input"
            />
            <button onClick={startGame} className="neon-button neon-button-cyan">
              üéÆ Start Game
            </button>
          </div>
        </div>
      ) : isPlaying ? (
        <div className="game-wrapper">
          <div className="game-container glassmorphic">
            <h1 className="neon-text">Code Optimization Challenge</h1>
            <h3 className="neon-text-small">
              Player: <span className="username">{username}</span> | Solved: {solvedCount}/{challenges.length} | 
              Score: <span className="score">{score}</span> | ‚è∞ {formatTime(gameTimeLeft)}
            </h3>
            <div className="challenge-card glassmorphic">
              <h3 className="neon-text">‚ö° Challenge {challengeId}:</h3>
              <pre>{challenges[challengeId - 1]}</pre>
            </div>
            <CodeMirror
              value={code}
              height="200px"
              extensions={[python()]}
              onChange={(value) => setCode(value)}
              placeholder="Optimize the code here..."
              theme="dark"
              className="neon-textarea"
            />
            <div className="button-group">
              <button onClick={evaluateCode} className="neon-button neon-button-blue">
                üöÄ Optimize
              </button>
            </div>
            {feedback && (
              <div className="feedback-card glassmorphic">
                <h2 className="neon-text">ü§ñ Feedback:</h2>
                <p>{feedback}</p>
              </div>
            )}
          </div>
        </div>
      ) : (
        <div className="end-screen-wrapper">
          <div className="end-screen glassmorphic">
            <h1 className="neon-text congrats">CONGRATULATIONS!</h1>
            <p className="neon-text-small">Final Score: {score}</p>
            <p className="neon-text-small">Challenges Solved: {solvedCount}/{challenges.length}</p>
            <button onClick={() => window.location.reload()} className="neon-button neon-button-green">
              Play Again
            </button>
          </div>
        </div>
      )}
    </div>
  );
}

export default App;
