import React, { useState, useEffect } from "react";
import "./App.css";

const App = () => {
  const [username, setUsername] = useState("");
  const [gameState, setGameState] = useState("login");
  const [challenge, setChallenge] = useState(null);
  const [score, setScore] = useState(0);
  const [error, setError] = useState("");

  const startGame = async () => {
    try {
      const response = await fetch("http://localhost:5001/register", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({ username }),
      });
      if (!response.ok) throw new Error("Registration failed");
      document.body.classList.add("fade-out");
      setTimeout(() => {
        setGameState("playing");
        fetchChallenge(true);
        document.body.classList.remove("fade-out");
      }, 500);
    } catch (err) {
      setError(`❌ ${err.message}`);
    }
  };

  const fetchChallenge = async (initial = false) => {
    try {
      const response = await fetch("http://localhost:5001/get_challenge", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({ username }),
      });
      if (!response.ok) throw new Error("Challenge fetch failed");
      const data = await response.json();
      if (data.completed) {
        setScore(data.final_score);
        setGameState("end");
      } else {
        setChallenge(data.challenge);
        if (initial) setGameState("playing");
      }
    } catch (err) {
      setError(`❌ ${err.message} - Check server logs`);
    }
  };

  return (
    <div className={`App ${gameState}`}>
      {gameState === "login" && (
        <div className="login">
          <div className="background-glow"></div>
          <input
            className="neon-input"
            value={username}
            onChange={(e) => setUsername(e.target.value)}
            placeholder="Enter username"
          />
          <button className="neon-button" onClick={startGame}>
            Start Game
          </button>
          {error && <p className="error">{error}</p>}
        </div>
      )}
      {gameState === "playing" && (
        <div className="game-ui slide-in">
          <div className="score-display">Score: {score}</div>
          <div className="challenge-panel">
            <pre>{challenge?.code}</pre>
            <button className="neon-button" onClick={() => fetchChallenge()}>
              Next Challenge
            </button>
          </div>
        </div>
      )}
      {gameState === "end" && (
        <div className="end-screen fade-in">
          <h1 className="congrats">CONGRATULATIONS!</h1>
          <div className="final-score">Final Score: {score}</div>
          <button
            className="neon-button"
            onClick={() => setGameState("login")}
          >
            Play Again
          </button>
        </div>
      )}
    </div>
  );
};

export default App;
