import { useState, useEffect, useCallback } from "react";
import Confetti from "react-confetti";
import CodeMirror from "@uiw/react-codemirror";
import { python } from "@codemirror/lang-python";
import "./App.css";

// Constants
const API_BASE_URL = process.env.REACT_APP_API_URL || "http://localhost:5001";
const GAME_DURATION = 900; // 15 minutes in seconds
const MAX_QUESTIONS = 10;

function App() {
  // State management
  const [username, setUsername] = useState(localStorage.getItem("codingChallengeUsername") || "");
  const [gameState, setGameState] = useState({
    isPlaying: false,
    isGameOver: false,
    loading: false,
    error: null,
    showConfetti: false,
    showLoginGlow: false,
    showReconnect: false,
    isApiHealthy: false
  });
  const [codeState, setCodeState] = useState({
    code: "",
    challenge: "",
    challengeId: null,
    solution: null,
    feedback: null,
    score: null
  });
  const [progress, setProgress] = useState({
    solvedCount: 0,
    totalScore: 0,
    gameTimeLeft: GAME_DURATION,
    scoreTracker: {}
  });

  // Destructure state for easier access
  const { isPlaying, isGameOver, loading, error, showConfetti, showLoginGlow, showReconnect, isApiHealthy } = gameState;
  const { code, challenge, challengeId, solution, feedback, score } = codeState;
  const { solvedCount, totalScore, gameTimeLeft, scoreTracker } = progress;

  // API service wrapper with timeout and error handling
  const apiRequest = useCallback(async (endpoint, body = {}) => {
    const controller = new AbortController();
    const timeoutId = setTimeout(() => controller.abort(), 8000); // 8 second timeout

    try {
      const response = await fetch(`${API_BASE_URL}${endpoint}`, {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({ username, ...body }),
        signal: controller.signal
      });

      clearTimeout(timeoutId);

      if (!response.ok) {
        const errorData = await response.json().catch(() => ({}));
        throw new Error(errorData.error || `HTTP error! status: ${response.status}`);
      }

      return await response.json();
    } catch (error) {
      clearTimeout(timeoutId);
      let errorMessage = "Network error";
      
      if (error.name === "AbortError") {
        errorMessage = "Request timeout - server is not responding";
      } else if (error.message.includes("Failed to fetch")) {
        errorMessage = "Cannot connect to server - check your network";
      } else {
        errorMessage = error.message;
      }

      throw new Error(errorMessage);
    }
  }, [username]);

  // Game management functions
  const resetGame = useCallback(() => {
    localStorage.removeItem("codingChallengeUsername");
    setGameState(prev => ({
      ...prev,
      isPlaying: false,
      isGameOver: false,
      error: "Session expired - please register again"
    }));
    setUsername("");
  }, []);

  const endGame = useCallback(() => {
    setGameState(prev => ({
      ...prev,
      isGameOver: true,
      showConfetti: true
    }));
  }, []);

  // API health check
  useEffect(() => {
    const checkHealth = async () => {
      try {
        await fetch(`${API_BASE_URL}/health`);
        setGameState(prev => ({ ...prev, isApiHealthy: true }));
      } catch {
        setGameState(prev => ({ ...prev, isApiHealthy: false }));
      }
    };
    
    checkHealth();
    const interval = setInterval(checkHealth, 30000);
    return () => clearInterval(interval);
  }, []);

  // Session validation
  useEffect(() => {
    const validateSession = async () => {
      if (isPlaying && username) {
        try {
          await apiRequest("/get_challenge");
        } catch (err) {
          if (err.message.includes("Invalid session")) {
            resetGame();
          }
        }
      }
    };

    const timer = setInterval(validateSession, 30000);
    return () => clearInterval(timer);
  }, [isPlaying, username, apiRequest, resetGame]);

  // Game timer
  useEffect(() => {
    if (!isPlaying || isGameOver) return;

    const interval = setInterval(() => {
      setProgress(prev => {
        if (prev.gameTimeLeft <= 1) {
          clearInterval(interval);
          endGame();
          return { ...prev, gameTimeLeft: 0 };
        }
        return { ...prev, gameTimeLeft: prev.gameTimeLeft - 1 };
      });
    }, 1000);

    return () => clearInterval(interval);
  }, [isPlaying, isGameOver, endGame]);

  // Game functions
  const startGame = useCallback(async () => {
    if (!username.trim()) {
      setGameState(prev => ({ ...prev, error: "⚠️ Please enter a username!" }));
      return;
    }

    setGameState(prev => ({ ...prev, loading: true, error: null }));

    try {
      await apiRequest("/register");
      localStorage.setItem("codingChallengeUsername", username);
      
      setGameState(prev => ({
        ...prev,
        isPlaying: true,
        showLoginGlow: true,
        showReconnect: false
      }));

      setTimeout(() => {
        setGameState(prev => ({ ...prev, showLoginGlow: false }));
        fetchChallenge(true);
      }, 1000);
    } catch (err) {
      setGameState(prev => ({
        ...prev,
        error: `❌ ${err.message}`,
        showReconnect: err.message.includes("cannot connect") || err.message.includes("timeout")
      }));
    } finally {
      setGameState(prev => ({ ...prev, loading: false }));
    }
  }, [username, apiRequest]);

  const fetchChallenge = useCallback(async (isFirst = false) => {
    if (solvedCount >= MAX_QUESTIONS || isGameOver) {
      endGame();
      return;
    }

    setGameState(prev => ({ ...prev, loading: true, error: null }));

    try {
      const data = await apiRequest("/get_challenge");

      if (data.completed) {
        endGame();
        return;
      }

      if (!data.challenge?.code || !data.challenge?.id) {
        throw new Error("Invalid challenge data received");
      }

      setCodeState(prev => ({
        ...prev,
        challenge: data.challenge.code,
        challengeId: data.challenge.id,
        feedback: null,
        score: null,
        solution: null,
        code: ""
      }));

      if (!isFirst) {
        setProgress(prev => ({
          ...prev,
          solvedCount: data.progress?.answered || prev.solvedCount + 1,
          gameTimeLeft: Math.floor(data.time_remaining || prev.gameTimeLeft)
        }));
      }
    } catch (err) {
      setGameState(prev => ({
        ...prev,
        error: `❌ ${err.message}`,
        showReconnect: err.message.includes("cannot connect") || err.message.includes("timeout")
      }));
      
      if (!isFirst) {
        setTimeout(fetchChallenge, 2000);
      }
    } finally {
      setGameState(prev => ({ ...prev, loading: false }));
    }
  }, [solvedCount, isGameOver, endGame, apiRequest]);

  const evaluateCode = useCallback(async () => {
    if (!code.trim()) {
      setGameState(prev => ({ ...prev, error: "⚠️ Enter code to evaluate!" }));
      return;
    }

    if (!code.includes("def ") || !code.includes("(") || !code.includes(")")) {
      setGameState(prev => ({ ...prev, error: "⚠️ Code must contain a function definition" }));
      return;
    }

    setGameState(prev => ({ ...prev, loading: true, error: null }));

    try {
      const data = await apiRequest("/evaluate", {
        code,
        challenge_code: challenge,
        challenge_id: challengeId
      });

      setCodeState(prev => ({ ...prev, feedback: data.feedback, score: data.score }));
      setProgress(prev => ({
        ...prev,
        totalScore: data.total_score,
        scoreTracker: { ...prev.scoreTracker, [challengeId]: data.score }
      }));
    } catch (err) {
      setGameState(prev => ({
        ...prev,
        error: `❌ ${err.message}`,
        showReconnect: err.message.includes("cannot connect") || err.message.includes("timeout")
      }));
    } finally {
      setGameState(prev => ({ ...prev, loading: false }));
    }
  }, [code, challenge, challengeId, apiRequest]);

  const fetchSolution = useCallback(async () => {
    setGameState(prev => ({ ...prev, loading: true, error: null }));

    try {
      const data = await apiRequest("/get_solution", { code: challenge });
      setCodeState(prev => ({ ...prev, solution: data.solution || "No solution available" }));
    } catch (err) {
      setGameState(prev => ({
        ...prev,
        error: `❌ ${err.message}`,
        showReconnect: err.message.includes("cannot connect") || err.message.includes("timeout")
      }));
    } finally {
      setGameState(prev => ({ ...prev, loading: false }));
    }
  }, [challenge, apiRequest]);

  const handleNextOrFinish = useCallback(() => {
    if (solvedCount >= MAX_QUESTIONS) {
      endGame();
      return;
    }
    if (!feedback) {
      setGameState(prev => ({ ...prev, error: "⚠️ Evaluate code first!" }));
      return;
    }
    fetchChallenge();
  }, [feedback, solvedCount, fetchChallenge, endGame]);

  // Helper functions
  const formatTime = (seconds) => {
    const mins = Math.floor(seconds / 60);
    const secs = seconds % 60;
    return `${mins}:${secs.toString().padStart(2, "0")}`;
  };

  // UI components
const LoginScreen = () => {
  const [inputValue, setInputValue] = useState("");
  const inputRef = useRef(null);

  const handleUsernameChange = (e) => {
    const value = e.target.value.replace(/[^a-zA-Z0-9_\- ]/g, '');
    setInputValue(value.slice(0, 20));
    setUsername(value.slice(0, 20)); // Update the main username state
  };

  return (
    <div className="login-wrapper">
      <div className="login-container glassmorphic">
        <h1 className="neon-title">REFACTOR ROYALE</h1>
        <div className="input-container">
          <input
            ref={inputRef}
            type="text"
            placeholder="ENTER USERNAME"
            value={inputValue}
            onChange={handleUsernameChange}
            className="neon-input"
            onKeyPress={(e) => e.key === 'Enter' && inputValue.trim() && startGame()}
          />
          <span className="input-highlight"></span>
        </div>
        <button 
          onClick={startGame} 
          className="neon-button neon-button-cyan"
          disabled={!inputValue.trim()}
        >
          <span className="button-text">START CODING</span>
          <span className="button-glow"></span>
        </button>
        {/* ... rest of your login screen code ... */}
      </div>
    </div>
  );
};

  const GameHeader = () => (
    <div className="game-header">
      <h1 className="neon-title">CODE OPTIMIZER</h1>
      <div className="stats-container">
        <div className="stat-box">
          <span className="stat-label">PLAYER</span>
          <span className="stat-value neon-cyan">{username}</span>
        </div>
        <div className="stat-box">
          <span className="stat-label">SOLVED</span>
          <span className="stat-value neon-purple">{solvedCount}/{MAX_QUESTIONS}</span>
        </div>
        <div className="stat-box">
          <span className="stat-label">SCORE</span>
          <span className="stat-value neon-green flicker">{totalScore}</span>
        </div>
        <div className="stat-box">
          <span className="stat-label">TIME</span>
          <span className="stat-value neon-red">{formatTime(gameTimeLeft)}</span>
        </div>
      </div>
      <div className="network-status">
        {isApiHealthy ? (
          <span className="status-indicator connected">● SERVER CONNECTED</span>
        ) : (
          <span className="status-indicator disconnected">● SERVER OFFLINE</span>
        )}
      </div>
    </div>
  );

  const ChallengeCard = () => (
  <div className="challenge-card glassmorphic">
    <div className="card-header">
      <h3>⚡ CHALLENGE {challenge.sequence || challengeId}</h3>
        <div className="difficulty-meter">
          {Array.from({ length: 5 }).map((_, i) => (
            <span key={i} className={i < (challengeId % 5) ? 'active' : ''}></span>
          ))}
        </div>
      </div>
      <pre className="neon-code">{challenge || "LOADING CHALLENGE..."}</pre>
    </div>
  );

  const GameOverScreen = () => (
    <div className="game-over glassmorphic">
      <div className="congratulations">
        <h2 className="neon-flicker">MISSION COMPLETE</h2>
        <div className="final-stats">
          <div className="stat-row">
            <span>FINAL SCORE</span>
            <span className="neon-cyan">{totalScore}</span>
          </div>
          <div className="stat-row">
            <span>CHALLENGES SOLVED</span>
            <span className="neon-purple">{solvedCount}/{MAX_QUESTIONS}</span>
          </div>
          <div className="stat-row">
            <span>TIME REMAINING</span>
            <span className="neon-green">{formatTime(gameTimeLeft)}</span>
          </div>
        </div>
      </div>
      
      <div className="restart-buttons">
        <button 
          onClick={() => window.location.reload()}
          className="neon-button neon-button-cyan"
        >
          <span className="button-text">NEW GAME</span>
          <span className="button-glow"></span>
        </button>
      </div>
    </div>
  );

  return (
    <div className={`app-container ${showLoginGlow ? 'login-glow' : ''}`}>
      {showConfetti && <Confetti recycle={false} numberOfPieces={500} />}
      
      {!isPlaying ? (
        <LoginScreen />
      ) : (
        <div className="game-wrapper">
          <div className="game-container glassmorphic">
            <GameHeader />
            
            {!isGameOver ? (
              <>
                <ChallengeCard />
                
                <div className="editor-container">
                  <CodeMirror
                    value={code}
                    height="200px"
                    extensions={[python()]}
                    onChange={(value) => setCodeState(prev => ({ ...prev, code: value }))}
                    placeholder="// OPTIMIZE THIS CODE..."
                    theme="dark"
                    className="neon-textarea"
                  />
                  <div className="editor-corner"></div>
                </div>
                
                <div className="button-group">
                  <button
                    onClick={evaluateCode}
                    disabled={loading}
                    className="neon-button neon-button-blue"
                  >
                    <span className="button-text">OPTIMIZE</span>
                    <span className="button-glow"></span>
                  </button>
                  <button
                    onClick={handleNextOrFinish}
                    disabled={!feedback || loading}
                    className="neon-button neon-button-magenta"
                  >
                    <span className="button-text">
                      {solvedCount === MAX_QUESTIONS - 1 ? "FINISH" : "NEXT"}
                    </span>
                    <span className="button-glow"></span>
                  </button>
                </div>
                
                {loading && (
                  <div className="loading-container">
                    <div className="neon-spinner">
                      <div className="spinner-sector spinner-sector-cyan"></div>
                      <div className="spinner-sector spinner-sector-magenta"></div>
                      <div className="spinner-sector spinner-sector-blue"></div>
                    </div>
                    <p className="neon-text">PROCESSING...</p>
                  </div>
                )}
                
                {error && (
                  <div className="error-container">
                    <p className="error">{error}</p>
                    {showReconnect && (
                      <button 
                        onClick={fetchChallenge}
                        className="neon-button neon-button-red"
                      >
                        <span className="button-text">RETRY</span>
                      </button>
                    )}
                  </div>
                )}
                
                {feedback && (
                  <div className="feedback-card glassmorphic">
                    <div className="card-header">
                      <h2>🤖 FEEDBACK</h2>
                      <div className="score-badge neon-green">
                        {score}/10
                      </div>
                    </div>
                    <p className="feedback-text">{feedback}</p>
                    <button 
                      onClick={fetchSolution} 
                      className="neon-button neon-button-purple"
                    >
                      <span className="button-text">VIEW SOLUTION</span>
                      <span className="button-glow"></span>
                    </button>
                  </div>
                )}
                
                {solution && (
                  <div className="solution-card glassmorphic">
                    <h2>💡 OPTIMAL SOLUTION</h2>
                    <pre className="neon-code">{solution}</pre>
                  </div>
                )}
              </>
            ) : (
              <GameOverScreen />
            )}
          </div>
          
          <div className="cyber-grid">
            {Array.from({ length: 20 }).map((_, i) => (
              <div key={i} className="grid-line"></div>
            ))}
          </div>
        </div>
      )}
    </div>
  );
}

export default App;
