import { useState, useEffect } from "react";
import Confetti from "react-confetti";
import CodeMirror from "@uiw/react-codemirror";
import { python } from "@codemirror/lang-python";
import "./App.css";

function App() {
  const [username, setUsername] = useState("");
  const [isPlaying, setIsPlaying] = useState(false);
  const [code, setCode] = useState("");
  const [feedback, setFeedback] = useState(null);
  const [score, setScore] = useState(null);
  const [solution, setSolution] = useState(null);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);
  const [challenge, setChallenge] = useState("");
  const [challengeId, setChallengeId] = useState(null);
  const [solvedCount, setSolvedCount] = useState(0);
  const [totalScore, setTotalScore] = useState(0);
  const [gameTimeLeft, setGameTimeLeft] = useState(900);
  const [isGameOver, setIsGameOver] = useState(false);
  const [showConfetti, setShowConfetti] = useState(false);
  const [showLoginGlow, setShowLoginGlow] = useState(false);
  const [sessionId, setSessionId] = useState(null);

  const API_BASE_URL = "http://localhost:5001";

  const startGame = async () => {
    if (!username.trim()) {
      setError("Please enter a username!");
      return;
    }
    setShowLoginGlow(true);
    try {
      const response = await fetch(`${API_BASE_URL}/register`, {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
        },
        body: JSON.stringify({ username }),
      });
      
      if (!response.ok) {
        const errorData = await response.json();
        throw new Error(errorData.error || "Registration failed");
      }
      
      const data = await response.json();
      setSessionId(data.session_id);
      
      setTimeout(() => {
        setIsPlaying(true);
        fetchChallenge(true);
        setShowLoginGlow(false);
      }, 1000);
    } catch (err) {
      setError(err.message);
    }
  };

  const fetchChallenge = async (isFirst = false) => {
    if (isGameOver) return;

    setLoading(true);
    setError(null);

    try {
      const response = await fetch(`${API_BASE_URL}/get_challenge`, {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
        },
        body: JSON.stringify({ 
          username,
          session_id: sessionId,
          question_number: solvedCount + 1
        }),
      });

      if (!response.ok) {
        const errorData = await response.json();
        throw new Error(errorData.error || "Failed to fetch challenge");
      }

      const data = await response.json();

      if (data.completed) {
        endGame(true);
        return;
      }

      setChallenge(data.challenge.code);
      setChallengeId(data.challenge.id);
      setFeedback(null);
      setScore(null);
      setSolution(null);
      setCode("");
      
      if (data.metadata?.time_remaining) {
        setGameTimeLeft(data.metadata.time_remaining);
      }
    } catch (err) {
      setError(`Failed to load challenge: ${err.message}`);
    } finally {
      setLoading(false);
    }
  };

  const evaluateCode = async () => {
    if (!code.trim()) {
      setError("Please enter code to evaluate!");
      return;
    }
    
    if (!code.includes("def ") || !code.includes("(") || !code.includes(")")) {
      setError("Code must contain a function definition");
      return;
    }
    
    setLoading(true);
    setError(null);
    try {
      const response = await fetch(`${API_BASE_URL}/evaluate`, {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
        },
        body: JSON.stringify({
          username,
          session_id: sessionId,
          code,
          challenge_code: challenge,
          challenge_id: challengeId,
        }),
      });
      
      if (!response.ok) {
        const errorData = await response.json();
        throw new Error(errorData.error || "Evaluation failed");
      }
      
      const data = await response.json();
      setFeedback(data.feedback);
      setScore(data.score);
      setTotalScore(data.total_score);
      
      if (data.is_completed) {
        endGame(true);
      }
    } catch (err) {
      setError(err.message);
    } finally {
      setLoading(false);
    }
  };

  const fetchSolution = async () => {
    setLoading(true);
    try {
      const response = await fetch(`${API_BASE_URL}/get_solution`, {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
        },
        body: JSON.stringify({ 
          username,
          session_id: sessionId,
          code: challenge,
        }),
      });
      
      if (!response.ok) {
        throw new Error("Failed to fetch solution");
      }
      
      const data = await response.json();
      setSolution(data.solution);
    } catch (err) {
      setError(err.message);
    } finally {
      setLoading(false);
    }
  };

  const handleNextOrFinish = async () => {
    if (solvedCount >= 9) {
      endGame(true);
      return;
    }
    if (!feedback) {
      setError("Please evaluate code first!");
      return;
    }
    await fetchChallenge();
    setSolvedCount(prev => prev + 1);
  };

  const endGame = (isComplete = false) => {
    setIsGameOver(true);
    if (isComplete) {
      setShowConfetti(true);
      setSolvedCount(10);
    }
  };

  const formatTime = (seconds) => {
    const mins = Math.floor(seconds / 60);
    const secs = seconds % 60;
    return `${mins}:${secs.toString().padStart(2, '0')}`;
  };

  useEffect(() => {
    if (!isPlaying || isGameOver) return;
    
    const timer = setInterval(() => {
      setGameTimeLeft(prev => {
        if (prev <= 1) {
          clearInterval(timer);
          endGame(false);
          return 0;
        }
        return prev - 1;
      });
    }, 1000);
    
    return () => clearInterval(timer);
  }, [isPlaying, isGameOver]);

  return (
    <div className={`app-container ${showLoginGlow ? 'login-glow' : ''}`}>
      {showConfetti && <Confetti recycle={false} numberOfPieces={500} />}
      
      {!isPlaying ? (
        <div className="login-wrapper">
          <div className="login-container glassmorphic">
            <h1 className="neon-title">REFACTOR ROYALE</h1>
            <input
              type="text"
              placeholder="ENTER USERNAME"
              value={username}
              onChange={(e) => setUsername(e.target.value)}
              className="neon-input"
              onKeyPress={(e) => e.key === 'Enter' && startGame()}
            />
            <button onClick={startGame} className="neon-button neon-button-cyan">
              START CODING
            </button>
            {error && <p className="error">{error}</p>}
          </div>
        </div>
      ) : (
        <div className="game-wrapper">
          <div className="game-container glassmorphic">
            <div className="game-header">
              <h1 className="neon-title">CODE OPTIMIZER</h1>
              <div className="stats-container">
                <div className="stat-box">
                  <span className="stat-label">PLAYER</span>
                  <span className="stat-value neon-cyan">{username}</span>
                </div>
                <div className="stat-box">
                  <span className="stat-label">SOLVED</span>
                  <span className="stat-value neon-purple">{solvedCount}/10</span>
                </div>
                <div className="stat-box">
                  <span className="stat-label">SCORE</span>
                  <span className="stat-value neon-green">{totalScore}</span>
                </div>
                <div className="stat-box">
                  <span className="stat-label">TIME</span>
                  <span className="stat-value neon-red">{formatTime(gameTimeLeft)}</span>
                </div>
              </div>
            </div>
            
            {loading ? (
              <div className="loading-spinner"></div>
            ) : isGameOver ? (
              <div className="game-over">
                <h2 className={solvedCount >= 10 ? "neon-text-green" : "neon-text-red"}>
                  {solvedCount >= 10 ? "MISSION COMPLETE!" : "TIME'S UP!"}
                </h2>
                <div className="final-stats">
                  <div className="stat-item">
                    <span className="stat-label">FINAL SCORE:</span>
                    <span className="stat-value">{totalScore}</span>
                  </div>
                  <div className="stat-item">
                    <span className="stat-label">CHALLENGES SOLVED:</span>
                    <span className="stat-value">{solvedCount}/10</span>
                  </div>
                </div>
                <button 
                  onClick={() => window.location.reload()} 
                  className="neon-button neon-button-magenta"
                >
                  NEW GAME
                </button>
              </div>
            ) : (
              <>
                <div className="challenge-card glassmorphic">
                  <h3>CHALLENGE</h3>
                  <pre className="neon-code">{challenge || "Loading challenge..."}</pre>
                </div>
                
                <div className="editor-container">
                  <CodeMirror
                    value={code}
                    height="200px"
                    extensions={[python()]}
                    onChange={setCode}
                    placeholder="# Optimize this Python function..."
                  />
                </div>
                
                <div className="button-group">
                  <button
                    onClick={evaluateCode}
                    disabled={loading}
                    className="neon-button neon-button-blue"
                  >
                    {loading ? "EVALUATING..." : "EVALUATE"}
                  </button>
                  <button
                    onClick={handleNextOrFinish}
                    disabled={!feedback || loading}
                    className="neon-button neon-button-magenta"
                  >
                    {solvedCount >= 9 ? "FINISH" : "NEXT"}
                  </button>
                </div>
                
                {error && <p className="error">{error}</p>}
                
                {feedback && (
                  <div className="feedback-card glassmorphic">
                    <h3>FEEDBACK</h3>
                    <p>{feedback}</p>
                    {score && <p className="score-display">Score: {score}/10</p>}
                    <button 
                      onClick={fetchSolution}
                      className="neon-button neon-button-cyan"
                    >
                      VIEW SOLUTION
                    </button>
                  </div>
                )}
                
                {solution && (
                  <div className="solution-card glassmorphic">
                    <h3>OPTIMAL SOLUTION</h3>
                    <pre>{solution}</pre>
                  </div>
                )}
              </>
            )}
          </div>
        </div>
      )}
    </div>
  );
}

export default App;
