import { useState, useEffect } from "react";
import Confetti from "react-confetti";
import CodeMirror from "@uiw/react-codemirror";
import { python } from "@codemirror/lang-python";
import "./App.css";

function App() {
  const [username, setUsername] = useState("");
  const [isPlaying, setIsPlaying] = useState(false);
  const [code, setCode] = useState("");
  const [feedback, setFeedback] = useState(null);
  const [score, setScore] = useState(null);
  const [solution, setSolution] = useState(null);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);
  const [challenge, setChallenge] = useState("");
  const [challengeId, setChallengeId] = useState(null);
  const [solvedCount, setSolvedCount] = useState(0);
  const [totalScore, setTotalScore] = useState(0);
  const [scoreTracker, setScoreTracker] = useState({});
  const [gameTimeLeft, setGameTimeLeft] = useState(900); // 15-minute game timer
  const [isGameOver, setIsGameOver] = useState(false);
  const [showConfetti, setShowConfetti] = useState(false);
  const [showLoginGlow, setShowLoginGlow] = useState(false);

  const startGame = () => {
    if (!username.trim()) {
      setError("⚠️ Please enter a username!");
      return;
    }
    setShowLoginGlow(true);
    setTimeout(() => {
      setIsPlaying(true);
      fetchChallenge(true);
      setShowLoginGlow(false);
    }, 1000);
  };

  const fetchChallenge = async (isFirst = false) => {
    if (solvedCount >= 10 || isGameOver) {
      endGame();
      return;
    }
    setLoading(true);
    setError(null);
    try {
      // Simulate API call with mock data
      await new Promise(resolve => setTimeout(resolve, 500));
      
      // Mock challenge data
      const mockChallenges = [
        {
          id: 1,
          code: `def sum_even_numbers(n):\n    total = 0\n    for i in range(n):\n        if i % 2 == 0:\n            total += i\n    return total`,
          difficulty: 3
        },
        {
          id: 2,
          code: `def find_duplicates(lst):\n    duplicates = []\n    for i in range(len(lst)):\n        for j in range(i+1, len(lst)):\n            if lst[i] == lst[j]:\n                duplicates.append(lst[i])\n    return duplicates`,
          difficulty: 5
        },
        {
          id: 3,
          code: `def is_prime(num):\n    if num < 2:\n        return False\n    for i in range(2, num):\n        if num % i == 0:\n            return False\n    return True`,
          difficulty: 4
        }
      ];
      
      const nextChallenge = mockChallenges[Math.min(solvedCount, mockChallenges.length - 1)];
      
      setChallenge(nextChallenge.code);
      setChallengeId(nextChallenge.id);
      setFeedback(null);
      setScore(null);
      setSolution(null);
      setCode("");
      if (!isFirst) setSolvedCount((prev) => prev + 1);
    } catch (err) {
      console.error("Fetch Error:", err.message);
      setError(`❌ ${err.message}`);
    } finally {
      setLoading(false);
    }
  };

  const evaluateCode = async () => {
    if (!code.trim()) {
      setError("⚠️ Enter code to evaluate!");
      return;
    }
    setLoading(true);
    setError(null);
    try {
      // Simulate evaluation with mock response
      await new Promise(resolve => setTimeout(resolve, 800));
      
      // Mock evaluation logic
      let mockScore = Math.floor(Math.random() * 5) + 5; // Random score between 5-10
      let mockFeedback = "";
      
      if (code.includes("for") && code.includes("range")) {
        mockFeedback = "✅ Great optimization using Python built-ins!";
      } else if (code.length < challenge.length * 0.7) {
        mockFeedback = "✅ Excellent! Your solution is more concise.";
        mockScore += 1;
      } else if (code.length > challenge.length * 1.3) {
        mockFeedback = "⚠️ Solution works but could be more concise.";
        mockScore -= 2;
      } else {
        mockFeedback = "✅ Correct solution!";
      }
      
      // Ensure score is between 1-10
      mockScore = Math.max(1, Math.min(10, mockScore));
      
      setFeedback(mockFeedback);
      setScore(mockScore);
      setTotalScore((prev) => prev + mockScore - (scoreTracker[challengeId] || 0));
      setScoreTracker((prev) => ({ ...prev, [challengeId]: mockScore }));
    } catch (err) {
      console.error("Evaluate Error:", err);
      setError("❌ Evaluation failed");
    } finally {
      setLoading(false);
    }
  };

  const fetchSolution = async () => {
    setLoading(true);
    setError(null);
    try {
      // Simulate API call with mock data
      await new Promise(resolve => setTimeout(resolve, 500));
      
      // Mock solutions
      const mockSolutions = {
        1: `def sum_even_numbers(n):\n    return sum(i for i in range(0, n, 2))`,
        2: `def find_duplicates(lst):\n    return list(set([x for x in lst if lst.count(x) > 1]))`,
        3: `def is_prime(num):\n    return num > 1 and all(num % i != 0 for i in range(2, int(num**0.5) + 1))`
      };
      
      setSolution(mockSolutions[challengeId] || "No solution available");
    } catch (err) {
      console.error("Solution Fetch Error:", err);
      setError("❌ Failed to fetch solution");
    } finally {
      setLoading(false);
    }
  };

  const handleNextOrFinish = async () => {
    if (solvedCount >= 10) {
      endGame();
      return;
    }
    if (!feedback) {
      setError("⚠️ Evaluate code first!");
      return;
    }
    await fetchChallenge();
  };

  const endGame = () => {
    setIsGameOver(true);
    setShowConfetti(true);
  };

  // Game timer
  useEffect(() => {
    if (!isPlaying || isGameOver) return;
    const interval = setInterval(() => {
      setGameTimeLeft((prev) => {
        if (prev <= 1) {
          clearInterval(interval);
          endGame();
          return 0;
        }
        return prev - 1;
      });
    }, 1000);
    return () => clearInterval(interval);
  }, [isPlaying, isGameOver]);

  const formatTime = (seconds) => {
    const minutes = Math.floor(seconds / 60);
    const secs = seconds % 60;
    return `${minutes}:${secs.toString().padStart(2, "0")}`;
  };

  return (
    <div className={`app-container ${showLoginGlow ? 'login-glow' : ''}`}>
      {showConfetti && <Confetti recycle={false} numberOfPieces={500} />}
      
      {!isPlaying ? (
        <div className="login-wrapper">
          <div className="login-container glassmorphic">
            <h1 className="neon-title">REFACTOR ROYALE</h1>
            <div className="input-container">
              <input
                type="text"
                placeholder="ENTER USERNAME"
                value={username}
                onChange={(e) => setUsername(e.target.value)}
                className="neon-input"
              />
              <span className="input-highlight"></span>
            </div>
            <button 
              onClick={startGame} 
              className="neon-button neon-button-cyan"
            >
              <span className="button-text">START CODING</span>
              <span className="button-glow"></span>
            </button>
            {error && <p className="error">{error}</p>}
            
            <div className="login-footer">
              <p className="neon-text">SINGLE PLAYER MODE</p>
              <p className="neon-text">15 MINUTE CHALLENGE</p>
            </div>
          </div>
          
          <div className="cyber-grid">
            {Array.from({ length: 20 }).map((_, i) => (
              <div key={i} className="grid-line"></div>
            ))}
          </div>
        </div>
      ) : (
        <div className="game-wrapper">
          <div className="game-container glassmorphic">
            <div className="game-header">
              <h1 className="neon-title">CODE OPTIMIZER</h1>
              <div className="stats-container">
                <div className="stat-box">
                  <span className="stat-label">PLAYER</span>
                  <span className="stat-value neon-cyan">{username}</span>
                </div>
                <div className="stat-box">
                  <span className="stat-label">SOLVED</span>
                  <span className="stat-value neon-purple">{solvedCount}/10</span>
                </div>
                <div className="stat-box">
                  <span className="stat-label">SCORE</span>
                  <span className="stat-value neon-green flicker">{totalScore}</span>
                </div>
                <div className="stat-box">
                  <span className="stat-label">TIME</span>
                  <span className="stat-value neon-red">{formatTime(gameTimeLeft)}</span>
                </div>
              </div>
            </div>
            
            {!isGameOver ? (
              <>
                <div className="challenge-card glassmorphic">
                  <div className="card-header">
                    <h3>⚡ CHALLENGE {challengeId}</h3>
                    <div className="difficulty-meter">
                      {Array.from({ length: 5 }).map((_, i) => (
                        <span 
                          key={i} 
                          className={i < (challengeId % 5) ? 'active' : ''}
                        ></span>
                      ))}
                    </div>
                  </div>
                  <pre className="neon-code">{challenge || "LOADING CHALLENGE..."}</pre>
                </div>
                
                <div className="editor-container">
                  <CodeMirror
                    value={code}
                    height="200px"
                    extensions={[python()]}
                    onChange={(value) => setCode(value)}
                    placeholder="// OPTIMIZE THIS CODE..."
                    theme="dark"
                    className="neon-textarea"
                  />
                  <div className="editor-corner"></div>
                </div>
                
                <div className="button-group">
                  <button
                    onClick={evaluateCode}
                    disabled={loading}
                    className="neon-button neon-button-blue"
                  >
                    <span className="button-text">OPTIMIZE</span>
                    <span className="button-glow"></span>
                  </button>
                  <button
                    onClick={handleNextOrFinish}
                    disabled={!feedback || loading}
                    className="neon-button neon-button-magenta"
                  >
                    <span className="button-text">
                      {solvedCount === 9 ? "FINISH" : "NEXT"}
                    </span>
                    <span className="button-glow"></span>
                  </button>
                </div>
                
                {loading && (
                  <div className="loading-container">
                    <div className="neon-spinner">
                      <div className="spinner-sector spinner-sector-cyan"></div>
                      <div className="spinner-sector spinner-sector-magenta"></div>
                      <div className="spinner-sector spinner-sector-blue"></div>
                    </div>
                    <p className="neon-text">PROCESSING...</p>
                  </div>
                )}
                
                {error && <p className="error">{error}</p>}
                
                {feedback && (
                  <div className="feedback-card glassmorphic">
                    <div className="card-header">
                      <h2>🤖 FEEDBACK</h2>
                      <div className="score-badge neon-green">
                        {score}/10
                      </div>
                    </div>
                    <p className="feedback-text">{feedback}</p>
                    <button 
                      onClick={fetchSolution} 
                      className="neon-button neon-button-purple"
                    >
                      <span className="button-text">VIEW SOLUTION</span>
                      <span className="button-glow"></span>
                    </button>
                  </div>
                )}
                
                {solution && (
                  <div className="solution-card glassmorphic">
                    <h2>💡 OPTIMAL SOLUTION</h2>
                    <pre className="neon-code">{solution}</pre>
                  </div>
                )}
              </>
            ) : (
              <div className="game-over glassmorphic">
                <div className="congratulations">
                  <h2 className="neon-flicker">MISSION COMPLETE</h2>
                  <div className="final-stats">
                    <div className="stat-row">
                      <span>FINAL SCORE</span>
                      <span className="neon-cyan">{totalScore}</span>
                    </div>
                    <div className="stat-row">
                      <span>CHALLENGES SOLVED</span>
                      <span className="neon-purple">{solvedCount}/10</span>
                    </div>
                    <div className="stat-row">
                      <span>TIME REMAINING</span>
                      <span className="neon-green">{formatTime(gameTimeLeft)}</span>
                    </div>
                  </div>
                </div>
                
                <div className="restart-buttons">
                  <button 
                    onClick={() => window.location.reload()}
                    className="neon-button neon-button-cyan"
                  >
                    <span className="button-text">NEW GAME</span>
                    <span className="button-glow"></span>
                  </button>
                </div>
              </div>
            )}
          </div>
          
          <div className="cyber-grid">
            {Array.from({ length: 20 }).map((_, i) => (
              <div key={i} className="grid-line"></div>
            ))}
          </div>
        </div>
      )}
    </div>
  );
}

export default App;
