import { useState, useEffect } from "react";
import Confetti from "react-confetti";
import CodeMirror from "@uiw/react-codemirror";
import { python } from "@codemirror/lang-python";
import "./App.css";

function App() {
  const [username, setUsername] = useState("");
  const [isPlaying, setIsPlaying] = useState(false);
  const [code, setCode] = useState("");
  const [feedback, setFeedback] = useState(null);
  const [score, setScore] = useState(null);
  const [solution, setSolution] = useState(null);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);
  const [challenge, setChallenge] = useState("");
  const [challengeId, setChallengeId] = useState(null);
  const [solvedCount, setSolvedCount] = useState(0);
  const [totalScore, setTotalScore] = useState(0);
  const [scoreTracker, setScoreTracker] = useState({});
  const [gameTimeLeft, setGameTimeLeft] = useState(900);
  const [isGameOver, setIsGameOver] = useState(false);
  const [showConfetti, setShowConfetti] = useState(false);
  const [showLoginGlow, setShowLoginGlow] = useState(false);

  const API_BASE_URL = "http://localhost:5001";

  const startGame = async () => {
    if (!username.trim()) {
      setError("âš ï¸ Please enter a username!");
      return;
    }
    setShowLoginGlow(true);
    try {
      const response = await fetch(`${API_BASE_URL}/register`, {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
        },
        body: JSON.stringify({ username }),
      });
      
      if (!response.ok) {
        const errorData = await response.json();
        throw new Error(errorData.error || "Registration failed");
      }
      
      setTimeout(() => {
        setIsPlaying(true);
        fetchChallenge(true);
        setShowLoginGlow(false);
      }, 1000);
    } catch (err) {
      setError(`âŒ ${err.message}`);
    }
  };

  const fetchChallenge = async (isFirst = false) => {
    if (isGameOver) {
      return;
    }

    setLoading(true);
    setError(null);

    try {
      const response = await fetch(`${API_BASE_URL}/get_challenge`, {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
        },
        body: JSON.stringify({ username }),
      });

      if (!response.ok) {
        const errorData = await response.json();
        throw new Error(errorData.error || "Failed to fetch challenge");
      }

      const data = await response.json();

      if (data.completed && data.progress?.answered >= 10) {
        endGame(true);
        return;
      }

      if (!data.challenge || !data.challenge.code || !data.challenge.id) {
        throw new Error("Invalid challenge data received");
      }

      setChallenge(data.challenge.code);
      setChallengeId(data.challenge.id);
      setFeedback(null);
      setScore(null);
      setSolution(null);
      setCode("");

      if (!isFirst) {
        const newSolvedCount = data.progress?.answered || solvedCount + 1;
        setSolvedCount(newSolvedCount);
        setGameTimeLeft(Math.floor(data.time_remaining || gameTimeLeft));
        
        if (newSolvedCount >= 10) {
          endGame(true);
        }
      }
    } catch (err) {
      console.error("Fetch Error:", err);
      setError(`âŒ ${err.message}`);
      if (!isFirst) {
        setTimeout(fetchChallenge, 2000);
      }
    } finally {
      setLoading(false);
    }
  };

  const evaluateCode = async () => {
    if (!code.trim()) {
      setError("âš ï¸ Enter code to evaluate!");
      return;
    }
    
    if (!code.includes("def ") || !code.includes("(") || !code.includes(")")) {
      setError("âš ï¸ Code must contain a function definition");
      return;
    }
    
    setLoading(true);
    setError(null);
    try {
      const response = await fetch(`${API_BASE_URL}/evaluate`, {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
        },
        body: JSON.stringify({
          username,
          code,
          challenge_code: challenge,
          challenge_id: challengeId,
        }),
      });
      
      if (!response.ok) {
        const errorData = await response.json();
        throw new Error(errorData.error || "Evaluation failed");
      }
      
      const data = await response.json();
      
      if (data.error) {
        throw new Error(data.error);
      }
      
      setFeedback(data.feedback);
      setScore(data.score);
      setTotalScore(data.total_score);
      
      setScoreTracker((prev) => ({
        ...prev,
        [challengeId]: data.score,
      }));
      
    } catch (err) {
      console.error("Evaluate Error:", err);
      setError(`âŒ ${err.message}`);
    } finally {
      setLoading(false);
    }
  };

  const fetchSolution = async () => {
    setLoading(true);
    setError(null);
    try {
      const response = await fetch(`${API_BASE_URL}/get_solution`, {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
        },
        body: JSON.stringify({ 
          code: challenge,
          username: username
        }),
      });
      
      if (!response.ok) {
        const errorData = await response.json();
        throw new Error(errorData.error || "Failed to fetch solution");
      }
      
      const data = await response.json();
      setSolution(data.solution || challenge);
    } catch (err) {
      console.error("Solution Error:", err);
      setError(`âŒ ${err.message}`);
      setSolution(challenge);
    } finally {
      setLoading(false);
    }
  };

  const handleNextOrFinish = async () => {
    if (!feedback) {
      setError("âš ï¸ Evaluate code first!");
      return;
    }
    await fetchChallenge();
  };

  const endGame = (isComplete = false) => {
    setIsGameOver(true);
    if (isComplete && solvedCount >= 10) {
      setShowConfetti(true);
    }
  };

  useEffect(() => {
    if (!isPlaying || isGameOver || solvedCount >= 10) return;
    
    const interval = setInterval(() => {
      setGameTimeLeft((prev) => {
        if (prev <= 1) {
          clearInterval(interval);
          endGame(false);
          return 0;
        }
        return prev - 1;
      });
    }, 1000);
    
    return () => clearInterval(interval);
  }, [isPlaying, isGameOver, solvedCount]);

  const formatTime = (seconds) => {
    const minutes = Math.floor(seconds / 60);
    const secs = seconds % 60;
    return `${minutes}:${secs.toString().padStart(2, "0")}`;
  };

  return (
    <div className={`app-container ${showLoginGlow ? 'login-glow' : ''}`}>
      {showConfetti && <Confetti recycle={false} numberOfPieces={500} />}
      
      {!isPlaying ? (
        <div className="login-wrapper">
          <div className="login-container glassmorphic">
            <h1 className="neon-title">REFACTOR ROYALE</h1>
            <div className="input-container">
              <input
                type="text"
                placeholder="ENTER USERNAME"
                value={username}
                onChange={(e) => setUsername(e.target.value)}
                className="neon-input"
                onKeyPress={(e) => e.key === 'Enter' && startGame()}
              />
              <span className="input-highlight"></span>
            </div>
            <button 
              onClick={startGame} 
              className="neon-button neon-button-cyan"
            >
              <span className="button-text">START CODING</span>
              <span className="button-glow"></span>
            </button>
            {error && <p className="error">{error}</p>}
            
            <div className="login-footer">
              <p className="neon-text">SINGLE PLAYER MODE</p>
              <p className="neon-text">15 MINUTE CHALLENGE</p>
            </div>
          </div>
          
          <div className="cyber-grid">
            {Array.from({ length: 20 }).map((_, i) => (
              <div key={i} className="grid-line"></div>
            ))}
          </div>
        </div>
      ) : (
        <div className="game-wrapper">
          <div className="game-container glassmorphic">
            <div className="game-header">
              <h1 className="neon-title">CODE OPTIMIZER</h1>
              <div className="stats-container">
                <div className="stat-box">
                  <span className="stat-label">PLAYER</span>
                  <span className="stat-value neon-cyan">{username}</span>
                </div>
                <div className="stat-box">
                  <span className="stat-label">SOLVED</span>
                  <span className="stat-value neon-purple">{solvedCount}/10</span>
                </div>
                <div className="stat-box">
                  <span className="stat-label">SCORE</span>
                  <span className="stat-value neon-green flicker">{totalScore}</span>
                </div>
                <div className="stat-box">
                  <span className="stat-label">TIME</span>
                  <span className="stat-value neon-red">{formatTime(gameTimeLeft)}</span>
                </div>
              </div>
            </div>
            
            {!isGameOver ? (
              <>
                <div className="challenge-card glassmorphic">
                  <div className="card-header">
                    <h3>âš¡ CHALLENGE {challengeId}</h3>
                    <div className="difficulty-meter">
                      {Array.from({ length: 5 }).map((_, i) => (
                        <span 
                          key={i} 
                          className={i < (challengeId % 5) ? 'active' : ''}
                        ></span>
                      ))}
                    </div>
                  </div>
                  <pre className="neon-code">{challenge || "LOADING CHALLENGE..."}</pre>
                </div>
                
                <div className="editor-container">
                  <CodeMirror
                    value={code}
                    height="200px"
                    extensions={[python()]}
                    onChange={(value) => setCode(value)}
                    placeholder="// OPTIMIZE THIS CODE..."
                    theme="dark"
                    className="neon-textarea"
                  />
                  <div className="editor-corner"></div>
                </div>
                
                <div className="button-group">
                  <button
                    onClick={evaluateCode}
                    disabled={loading}
                    className="neon-button neon-button-blue"
                  >
                    <span className="button-text">OPTIMIZE</span>
                    <span className="button-glow"></span>
                  </button>
                  <button
                    onClick={handleNextOrFinish}
                    disabled={!feedback || loading}
                    className="neon-button neon-button-magenta"
                  >
                    <span className="button-text">
                      {solvedCount === 9 ? "FINISH" : "NEXT"}
                    </span>
                    <span className="button-glow"></span>
                  </button>
                </div>
                
                {loading && (
                  <div className="loading-container">
                    <div className="neon-spinner">
                      <div className="spinner-sector spinner-sector-cyan"></div>
                      <div className="spinner-sector spinner-sector-magenta"></div>
                      <div className="spinner-sector spinner-sector-blue"></div>
                    </div>
                    <p className="neon-text">PROCESSING...</p>
                  </div>
                )}
                
                {error && <p className="error">{error}</p>}
                
                {feedback && (
                  <div className="feedback-card glassmorphic">
                    <div className="card-header">
                      <h2>ðŸ¤– FEEDBACK</h2>
                      <div className="score-badge neon-green">
                        {score}/10
                      </div>
                    </div>
                    <p className="feedback-text">{feedback}</p>
                    <button 
                      onClick={fetchSolution} 
                      className="neon-button neon-button-purple"
                    >
                      <span className="button-text">VIEW SOLUTION</span>
                      <span className="button-glow"></span>
                    </button>
                  </div>
                )}
                
                {solution && (
                  <div className="solution-card glassmorphic">
                    <h2>ðŸ’¡ OPTIMAL SOLUTION</h2>
                    <pre className="neon-code">{solution}</pre>
                  </div>
                )}
              </>
            ) : (
              <div className="game-over glassmorphic">
                <div className="congratulations">
                  {solvedCount >= 10 ? (
                    <>
                      <h2 className="neon-flicker">MISSION COMPLETE</h2>
                      <div className="final-stats">
                        <div className="stat-row">
                          <span>FINAL SCORE</span>
                          <span className="neon-cyan">{totalScore}</span>
                        </div>
                        <div className="stat-row">
                          <span>CHALLENGES SOLVED</span>
                          <span className="neon-purple">10/10</span>
                        </div>
                        <div className="stat-row">
                          <span>TIME REMAINING</span>
                          <span className="neon-green">{formatTime(gameTimeLeft)}</span>
                        </div>
                      </div>
                    </>
                  ) : (
                    <>
                      <h2 className="neon-red">TIME'S UP!</h2>
                      <div className="final-stats">
                        <div className="stat-row">
                          <span>FINAL SCORE</span>
                          <span className="neon-cyan">{totalScore}</span>
                        </div>
                        <div className="stat-row">
                          <span>CHALLENGES SOLVED</span>
                          <span className="neon-purple">{solvedCount}/10</span>
                        </div>
                      </div>
                    </>
                  )}
                </div>
                
                <div className="restart-buttons">
                  <button 
                    onClick={() => window.location.reload()}
                    className="neon-button neon-button-cyan"
                  >
                    <span className="button-text">NEW GAME</span>
                    <span className="button-glow"></span>
                  </button>
                </div>
              </div>
            )}
          </div>
          
          <div className="cyber-grid">
            {Array.from({ length: 20 }).map((_, i) => (
              <div key={i} className="grid-line"></div>
            ))}
          </div>
        </div>
      )}
    </div>
  );
}

export default App;
