import { useState, useEffect, useRef } from "react";
import { Canvas, useFrame } from "@react-three/fiber";
import { OrbitControls, Text, Float, Stars } from "@react-three/drei";
import * as THREE from "three";
import Confetti from "react-confetti";
import CodeMirror from "@uiw/react-codemirror";
import { python } from "@codemirror/lang-python";
import gsap from "gsap";
import "./App.css";

// 3D Floating Cube Component
function FloatingCube({ position, color }) {
  const meshRef = useRef();
  useFrame((state, delta) => {
    meshRef.current.rotation.x += delta * 0.5;
    meshRef.current.rotation.y += delta * 0.3;
  });
  return (
    <mesh ref={meshRef} position={position}>
      <boxGeometry args={[1, 1, 1]} />
      <meshStandardMaterial color={color} />
    </mesh>
  );
}

// 3D Floating Text Component
function FloatingText({ text, position, size = 0.5, color = "white" }) {
  return (
    <Float speed={2} rotationIntensity={0.5} floatIntensity={1}>
      <Text
        position={position}
        fontSize={size}
        color={color}
        font="https://fonts.gstatic.com/s/raleway/v14/1Ptrg8zYS_SKggPNwK4vaqI.woff"
      >
        {text}
      </Text>
    </Float>
  );
}

// 3D Trophy Component for End Screen
function Trophy({ position, rank }) {
  const meshRef = useRef();
  useFrame((state, delta) => {
    meshRef.current.rotation.y += delta * 0.2;
  });
  const color = rank === "gold" ? "#FFD700" : rank === "silver" ? "#C0C0C0" : "#CD7F32";
  return (
    <mesh ref={meshRef} position={position}>
      <coneGeometry args={[1, 2, 32]} />
      <meshStandardMaterial color={color} metalness={0.8} roughness={0.2} />
    </mesh>
  );
}

function App() {
  const [username, setUsername] = useState("");
  const [isPlaying, setIsPlaying] = useState(false);
  const [code, setCode] = useState("");
  const [feedback, setFeedback] = useState(null);
  const [score, setScore] = useState(null);
  const [solution, setSolution] = useState(null);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);
  const [challenge, setChallenge] = useState("");
  const [challengeId, setChallengeId] = useState(null);
  const [solvedCount, setSolvedCount] = useState(0);
  const [totalScore, setTotalScore] = useState(0);
  const [gameTimeLeft, setGameTimeLeft] = useState(900); // 15 minutes = 900 seconds
  const [isGameOver, setIsGameOver] = useState(false);
  const [showConfetti, setShowConfetti] = useState(false);
  const codeBlockRef = useRef(null);
  const scoreRef = useRef(null);

  const startGame = async () => {
    if (!username.trim()) {
      setError("‚ö†Ô∏è Please enter a username!");
      return;
    }
    setLoading(true);
    setError(null);
    try {
      const response = await fetch("http://localhost:5000/register", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({ username }),
      });
      if (!response.ok) throw new Error("Registration failed");
      setIsPlaying(true);
      fetchChallenge(true);
    } catch (err) {
      setError(`‚ùå ${err.message} - Check server`);
    } finally {
      setLoading(false);
    }
  };

  const fetchChallenge = async (isFirst = false) => {
    if (solvedCount >= 10 || isGameOver) {
      setIsGameOver(true);
      setShowConfetti(true);
      return;
    }
    setLoading(true);
    setError(null);
    try {
      const response = await fetch("http://localhost:5000/get_challenge", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({ username }),
      });
      if (!response.ok) throw new Error("Challenge fetch failed");
      const data = await response.json();
      if (data.completed) {
        setIsGameOver(true);
        setShowConfetti(true);
      } else {
        setChallenge(data.challenge.code);
        setChallengeId(data.challenge.id);
        setFeedback(null);
        setScore(null);
        setSolution(null);
        setCode("");
        if (!isFirst) setSolvedCount((prev) => prev + 1);
      }
    } catch (err) {
      setError(`‚ùå ${err.message} - Check server logs`);
    } finally {
      setLoading(false);
    }
  };

  const evaluateCode = async () => {
    if (!code.trim()) {
      setError("‚ö†Ô∏è Enter code to evaluate!");
      return;
    }
    setLoading(true);
    setError(null);
    // Flip animation for code block
    gsap.to(codeBlockRef.current, { rotationY: 180, duration: 0.5 });
    try {
      const response = await fetch("http://localhost:5000/evaluate", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({ username, code, challenge_code: challenge, challenge_id: challengeId }),
      });
      if (!response.ok) throw new Error("Evaluation failed");
      const data = await response.json();
      setFeedback(data.feedback);
      setScore(data.score);
      setTotalScore(data.total_score);
      if (data.score > 0) {
        gsap.fromTo(
          scoreRef.current,
          { scale: 1.5, opacity: 0 },
          { scale: 1, opacity: 1, duration: 0.5, ease: "bounce.out" }
        );
      }
      if (data.is_completed) {
        setIsGameOver(true);
        setShowConfetti(true);
      } else {
        fetchChallenge();
      }
    } catch (err) {
      setError(`‚ùå ${err.message} - Check server`);
    } finally {
      setLoading(false);
      gsap.to(codeBlockRef.current, { rotationY: 0, duration: 0.5 });
    }
  };

  const fetchSolution = async () => {
    setLoading(true);
    setError(null);
    try {
      const response = await fetch("http://localhost:5000/get_solution", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({ code: challenge }),
      });
      if (!response.ok) throw new Error("Solution fetch failed");
      const data = await response.json();
      setSolution(data.solution);
    } catch (err) {
      setError(`‚ùå ${err.message} - Check server`);
    } finally {
      setLoading(false);
    }
  };

  // Game timer
  useEffect(() => {
    if (!isPlaying || isGameOver) return;
    const interval = setInterval(() => {
      setGameTimeLeft((prev) => {
        if (prev <= 1) {
          clearInterval(interval);
          setIsGameOver(true);
          setShowConfetti(true);
          return 0;
        }
        return prev - 1;
      });
    }, 1000);
    return () => clearInterval(interval);
  }, [isPlaying, isGameOver]);

  const formatTime = (seconds) => {
    const minutes = Math.floor(seconds / 60);
    const secs = seconds % 60;
    return `${minutes}:${secs.toString().padStart(2, "0")}`;
  };

  return (
    <div className="app-container" style={{ backgroundColor: "#000B18" }}>
      {showConfetti && <Confetti />}
      <div className="three-canvas-container">
        <Canvas camera={{ position: [0, 0, 10], fov: 50 }}>
          <ambientLight intensity={0.5} />
          <pointLight position={[10, 10, 10]} />
          <Stars radius={100} depth={50} count={5000} factor={4} saturation={0} fade speed={1} />
          {!isPlaying ? (
            <>
              <FloatingText text="Refactor Royale" position={[0, 2, 0]} size={1} color="#00FFFF" />
              <FloatingCube position={[-3, 0, 0]} color="#FF00FF" />
              <FloatingCube position={[3, 0, 0]} color="#FFFF00" />
            </>
          ) : isGameOver ? (
            <>
              <FloatingText text="Game Over!" position={[0, 3, 0]} size={1} color="#FF00FF" />
              <Trophy position={[0, 0, 0]} rank={totalScore >= 80 ? "gold" : totalScore >= 50 ? "silver" : "bronze"} />
            </>
          ) : (
            <>
              <FloatingText text={`Challenge ${challengeId || "Loading"}`} position={[0, 5, 0]} size={0.7} color="#00FF00" />
              <FloatingText text={`Score: ${totalScore}`} position={[-5, 4, 0]} size={0.5} color="#FFFF00" />
              <FloatingText text={`Time: ${formatTime(gameTimeLeft)}`} position={[5, 4, 0]} size={0.5} color="#FF00FF" />
            </>
          )}
          <OrbitControls enableZoom={false} />
        </Canvas>
      </div>

      {!isPlaying ? (
        <div className="login-wrapper">
          <div className="login-container glassmorphic neon-border">
            <input
              type="text"
              placeholder="Enter your username"
              value={username}
              onChange={(e) => setUsername(e.target.value)}
              className="neon-input"
              style={{ transform: "perspective(1000px) rotateX(10deg)" }}
            />
            <button
              onClick={startGame}
              className="neon-button neon-button-cyan"
              style={{ transform: "perspective(1000px) translateZ(20px)" }}
              onMouseEnter={(e) => gsap.to(e.target, { translateZ: 30, duration: 0.2 })}
              onMouseLeave={(e) => gsap.to(e.target, { translateZ: 20, duration: 0.2 })}
              onMouseDown={(e) => gsap.to(e.target, { translateZ: 10, duration: 0.1 })}
              onMouseUp={(e) => gsap.to(e.target, { translateZ: 20, duration: 0.1 })}
            >
              üéÆ Start Game
            </button>
            {loading && <div className="neon-spinner"></div>}
            {error && <p className="error neon-text">{error}</p>}
          </div>
        </div>
      ) : (
        <div className="game-wrapper">
          <div className="game-container glassmorphic neon-border" style={{ transform: "perspective(1000px) translateZ(20px)" }}>
            <h3 className="neon-text">
              Player: <span className="username">{username}</span> | Solved: {solvedCount}/10 | Score: <span ref={scoreRef} className="score">{totalScore}</span>
            </h3>
            <div className="challenge-card glassmorphic neon-border" style={{ transform: "perspective(1000px) translateZ(10px)" }}>
              <h3 className="neon-text">‚ö° Challenge {challengeId || "Loading"}:</h3>
              <pre>{challenge || "Loading..."}</pre>
            </div>
            <div ref={codeBlockRef} style={{ transform: "perspective(1000px)" }}>
              <CodeMirror
                value={code}
                height="200px"
                extensions={[python()]}
                onChange={(value) => setCode(value)}
                placeholder="Optimize the code here..."
                theme="dark"
                className="neon-textarea"
              />
            </div>
            <div className="button-group">
              <button
                onClick={evaluateCode}
                disabled={loading}
                className="neon-button neon-button-blue"
                onMouseEnter={(e) => gsap.to(e.target, { scale: 1.1, boxShadow: "0 0 15px #00FFFF", duration: 0.2 })}
                onMouseLeave={(e) => gsap.to(e.target, { scale: 1, boxShadow: "0 0 5px #00FFFF", duration: 0.2 })}
              >
                üöÄ Optimize
              </button>
              <button
                onClick={fetchSolution}
                disabled={loading}
                className="neon-button neon-button-purple"
                onMouseEnter={(e) => gsap.to(e.target, { scale: 1.1, boxShadow: "0 0 15px #FF00FF", duration: 0.2 })}
                onMouseLeave={(e) => gsap.to(e.target, { scale: 1, boxShadow: "0 0 5px #FF00FF", duration: 0.2 })}
              >
                üëÄ View Solution
              </button>
            </div>
            {loading && (
              <div className="loading-3d">
                <Canvas>
                  <ambientLight intensity={0.5} />
                  <pointLight position={[10, 10, 10]} />
                  <mesh rotation={[0, 0, 0]}>
                    <torusGeometry args={[1, 0.4, 16, 32]} />
                    <meshStandardMaterial color="#00FFFF" />
                  </mesh>
                </Canvas>
              </div>
            )}
            {error && <p className="error neon-text">{error}</p>}
            {feedback && (
              <div className="feedback-card glassmorphic neon-border" style={{ transform: "perspective(1000px) translateZ(10px)" }}>
                <h2 className="neon-text">ü§ñ Feedback:</h2>
                <p>{feedback}</p>
                <p className="score neon-text">Score: {score}</p>
              </div>
            )}
            {solution && (
              <div className="solution-card glassmorphic neon-border" style={{ transform: "perspective(1000px) translateZ(10px)" }}>
                <h2 className="neon-text">üí° Solution:</h2>
                <pre>{solution}</pre>
              </div>
            )}
          </div>
        </div>
      )}
      {isGameOver && (
        <div className="game-over-wrapper">
          <div className="game-over glassmorphic neon-border" style={{ transform: "perspective(1000px) translateZ(20px)" }}>
            <h2 className="neon-text">üèÜ Game Over!</h2>
            <p className="neon-text">
              Final Score:{" "}
              <span ref={scoreRef} className="neon-text">
                {totalScore}
              </span>
            </p>
            <p className="neon-text">Challenges Solved: {solvedCount}/10</p>
            <button
              onClick={() => window.location.reload()}
              className="neon-button neon-button-green"
              onMouseEnter={(e) => gsap.to(e.target, { scale: 1.1, boxShadow: "0 0 15px #00FF00", duration: 0.2 })}
              onMouseLeave={(e) => gsap.to(e.target, { scale: 1, boxShadow: "0 0 5px #00FF00", duration: 0.2 })}
            >
              Play Again
            </button>
          </div>
        </div>
      )}
    </div>
  );
}

export default App;
